package com.nerdemo;

public class Result {
    
    private String word;
    private String answer;
    
    public Result() {
        // TODO Auto-generated constructor stub
    }

    public Result(String word, String answer) {
        super();
        this.word = word;
        this.answer = answer;
    }

    public String getWord() {
        return word;
    }

    public void setWord(String word) {
        this.word = word;
    }

    public String getAnswer() {
        return answer;
    }

    public void setAnswer(String answer) {
        this.answer = answer;
    }
    
    
}
